content:
{% schema %}
  {
    "name": "Hero Banner",
    "settings": [
      {
        "type": "text",
        "id": "title",
        "label": "Title",
        "default": "Welcome to Our Store"
      },
      {
        "type": "textarea",
        "id": "description",
        "label": "Description",
        "default": "Discover amazing products"
      },
      {
        "type": "image_picker",
        "id": "image",
        "label": "Background Image"
      },
      {
        "type": "text",
        "id": "button_text",
        "label": "Button Text",
        "default": "Shop Now"
      },
      {
        "type": "url",
        "id": "button_link",
        "label": "Button Link"
      }
    ]
  }
{% endschema %}

<div class="hero-banner" style="background-image: url('{{ section.settings.image | img_url: 'master' }}');">
  <div class="hero-content">
    <h1>{{ section.settings.title }}</h1>
    <p>{{ section.settings.description }}</p>
    {% if section.settings.button_link != blank %}
      <a href="{{ section.settings.button_link }}" class="btn btn-primary">
        {{ section.settings.button_text }}
      </a>
    {% endif %}
  </div>
</div>

<style>
  .hero-banner {
    position: relative;
    min-height: 500px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-size: cover;
    background-position: center;
  }
  .hero-content {
    text-align: center;
    color: white;
    z-index: 1;
  }
  .hero-content h1 {
    font-size: 3rem;
    margin-bottom: 1rem;
  }
  .btn-primary {
    display: inline-block;
    padding: 12px 24px;
    background: #000;
    color: white;
    text-decoration: none;
    border-radius: 4px;
  }
</style>
